import React, { useState, useEffect } from "react";
import { Button, Card, Col, Image, Row, Switch } from "antd";
import { useTranslation } from "next-i18next";

export default function ScratchMapImage({ selected }) {
    const [autoreload, setAutoreload] = useState(false);
    const [apiCall, setApiCall] = useState("");

    const { t } = useTranslation();

    useEffect(() => {
        console.log("useEffect()");
        if (autoreload) {
            generateApiCall();
        }
    }, [selected, autoreload]);

    const generateApiCall = () => {
        let filter = ["!in", "iso_3166_1_alpha_3"].concat(selected);
        let fogLayer = {
            id: "country-boundaries-fog",
            source: {
                type: "vector",
                url: "mapbox://mapbox.country-boundaries-v1",
            },
            "source-layer": "country_boundaries",
            type: "fill",
            paint: {
                "fill-color": "#a5b1c2",
                "fill-opacity": 1.0,
            },
            filter: filter,
        };

        const mapboxBaseUrl = "https://api.mapbox.com";
        // TODO: Think about better protecting the credentials, i.e. make the call on the server side (--> remove the NEXT_PUBLIC_ prefix after doing)
        const username = process.env.NEXT_PUBLIC_MAPBOX_USERNAME;
        const style_id = process.env.NEXT_PUBLIC_MAPBOX_STYLE_ID;
        const access_token = process.env.NEXT_PUBLIC_MAPBOX_ACCESS_TOKEN;
        const lon_min = -180;
        const lat_min = -70;
        const lon_max = 180;
        const lat_max = 80;
        const bbox = [lon_min, lat_min, lon_max, lat_max];
        const width = 1280;
        const height = 1280;
        const showLogo = true;
        const showAttribution = true;

        setApiCall(
            `${mapboxBaseUrl}/styles/v1/${username}/${style_id}/static/[${bbox}]/${width}x${height}@2x?access_token=${access_token}&logo=${showLogo}&attribution=${showAttribution}&addlayer=${encodeURIComponent(
                JSON.stringify(fogLayer),
            )}&before_layer=transit-label`,
        );
    };

    const fallback =
        "data:image/png;base64,";

    return (
        <>
            <Row gutter={16}>
                <Col span={4}>
                    <Button onClick={generateApiCall}>{t("Update Map")}</Button>
                </Col>{" "}
                <Col span={6}>
                    <Switch
                        defaultChecked={autoreload}
                        onChange={(checked) => {
                            setAutoreload(checked);
                        }}
                    />
                    {t("Autoreload")}
                </Col>
            </Row>
            <Row gutter={16}>
                <Col span={16}>
                    <Image width={800} src={apiCall} fallback={fallback} />
                </Col>
            </Row>
            {/* <p>apiCall: {apiCall}</p> */}
        </>
    );
}
